import styled from "styled-components";
import { theme } from "../styles/theme";

export const Link = styled.a`
    font-weight: 400;
    font-size: 14px;
    letter-spacing: 1px;
    text-transform: uppercase;
    padding: 10px;
    position: relative;
    z-index: 0;    

    &::before {
        content: "";
        height: 10px;
        background-color: ${theme.colors.accent};

        position: absolute;
        bottom: 5px;
        left: 0;
        right: 0;
        z-index: -1;

        display: none;

    }

    &:hover {
        &::before {
            display: inline-block;
        }
    }
`;
