import React from "react";
import styled from "styled-components";
import {theme} from "../../../styles/theme";

export const HeaderMenu = (props: { menuItems: Array<string> }) => {
    return (
        <StyledMobileMenu>
            <BurgerButton>
                <span>
                    
                </span>
            </BurgerButton>

            <ul>
                {props.menuItems.map((item: string, index: number) => {
                    return (
                        <ListItem key={index}>
                            <Link href="#">
                                {item}
                                <Mask>
                                    <span>{item}</span>
                                </Mask>
                                <Mask>
                                    <span>{item}</span>
                                </Mask>
                            </Link>
                        </ListItem>
                    );
                })}
            </ul>
        </StyledMobileMenu>
    );
};

const StyledMobileMenu = styled.nav`
  ul {
    display: flex;
    gap: 30px;
    justify-content: center;
  }

  @media ${theme.media.tablet} {
    display: none;
  }
`;

const BurgerButton = styled.button`
  position: fixed;
  width: 200px;
  height: 100px;
  top: 100px;
  right: 100px;
  
  span {
    display: block;
    width: 36px;
    height: 2px;
    color: ${theme.colors.font};
    position: absolute;
    left: 40px;
    bottom: 50px;
    
    &::before {
      content:"";
      display: block;
      width: 36px;
      height: 2px;
      color: ${theme.colors.font};
      position: absolute;
      transform: translateY(-10px);
    }

    &::after {
      content:""; 
      display: block;
      width: 24px;
      height: 2px;
      color: ${theme.colors.font};
      position: absolute;
      transform: translateY(10px);
    }
  }
`;

const Link = styled.a`
  font-family: "Josefin Sans", sans-serif;
  font-weight: 400;
  font-size: 30px;
  text-align: center;
  color: transparent;
`;

const Mask = styled.span`
  position: absolute;
  top: 0;
  left: 0;
  display: inline-block;
  height: 50%;
  overflow-y: hidden;
  color: ${theme.colors.accent};

  & + & {
    top: 50%;

    span {
      display: inline-block;
      transform: translateY(-50%);
    }
  }
`;

const ListItem = styled.li`
  position: relative;

  &::before {
    content: "";
    display: none;
    height: 1.5px;
    background-color: ${theme.colors.accent};
    position: absolute;
    top: 50%;
    left: -10px;
    right: -10px;
    z-index: 1;
  }

  &:hover {

    &::before {
      display: inline-block;
    }

    ${Mask} {
      transform: skewX(12deg) translateX(5px);
      color: ${theme.colors.font};

      & + ${Mask} {
        transform: skewX(12deg) translateX(-5px);
      }

      transition-duration: .15s;
    }
  }
`;
